// bucket.js
import { db } from "../../firebase";
import {
  collection,
  doc,
  getDoc,
  getDocs,
  addDoc,
  updateDoc,
  deleteDoc,
} from "firebase/firestore";

// Actions
const LOAD = "bucket/LOAD";
const CREATE = "bucket/CREATE";
const DELETE = "bucket/DELETE";
const UPDATE = "bucket/UPDATE";

const initialState = {
  list: [],
};
export function loadBucket(bucket_list) {
  return { type: LOAD, bucket_list };
}
// Action Creators
export function createBucket(bucket) {
  return { type: CREATE, bucket: bucket };
}

export function deleteBucket(bucket_index) {
  return { type: DELETE, bucket_index };
}

export function updateBucket(bucket_index) {
  return { type: UPDATE, bucket_index };
}

//middlewares
export const loadBucketFB = () => {
  return async function (dispatch) {
    const bucket_data = await getDocs(collection(db, "bucket"));

    let bucket_list = [];

    bucket_data.forEach((b) => {
      bucket_list.push({ id: b.id, ...b.data() });
    });
    dispatch(loadBucket(bucket_list));
  };
};

export const addBucketFB = (bucket) => {
  return async function (dispatch) {
    const docRef = await addDoc(collection(db, "bucket"), bucket);
    // const _bucket = await getDoc(docRef);
    const bucket_data = { id: docRef.id, ...bucket };

    dispatch(createBucket(bucket_data));
  };
};

export const updateBucketFB = (bucket_id) => {
  return async function(dispatch, getState) {
    const docRef = doc(db, 'bucket', bucket_id);
    await updateDoc(docRef, {completed:true});

    console.log(getState().bucket);
    const _bucket_list = getState().bucket.list;
    const bucket_index = _bucket_list.findIndex((b) => {
      return b.id === bucket_id;
    });
    dispatch(updateBucket(bucket_index))
  }
}

//Reducer
export default function reducer(state = initialState, action = {}) {
  if (action.type === "bucket/LOAD") {
    return { list: action.bucket_list };
  } else if (action.type === "bucket/CREATE") {
    const new_bucket_list = [...state.list, action.bucket];
    return { list: new_bucket_list };
  } else if (action.type === "bucket/DELETE") {
    const new_bucket_list = state.list.filter((l, idx) => {
      return parseInt(action.bucket_index) !== idx;
    });
    return { list: new_bucket_list };
  } else if (action.type === "bucket/UPDATE") {
    const new_bucket_list = state.list.map((l, idx) => {
      if (Number(action.bucket_index) === idx) {
        return { ...l, completed: true };
      } else {
        return l;
      }
    });
    return { list: new_bucket_list };
  } else {
    return state;
  }
}
