import React from 'react';
import BucketList from './BucketList';
import './style.css';
import styled from 'styled-components';

class App extends React.Component{
  constructor(props){
      super(props);

      this.state = {
        list : ["영화간 가기", "매일 책읽기", "수영 배우기"],
    }
  }

  render(){
    return(
      <div className="App">
          <Container>
            <Title>버킷 리스트</Title>
            <Line/>
            <BucketList list={this.state.list}/>
          </Container>
      </div>
    );
  }

}

const Container = styled.div`
  background-color: #fff;
  width: 50vw;
  height: 80vh;
  max-width: 350px;
  margin:auto;
  padding: 16px;
  border:1px solid #ddd;
  border-radius: 5px;
`;

const Title = styled.h1`
  color: slateblue;
  text-align:center;
`;

const Line = styled.hr`
  margin: 16px 0px; 
`;

export default App;
